import React, { Component } from 'react';
import Habit from './habit';
import Navbar from './navbar';

class Habits extends Component {
    handleIncrement = (habit) => { //callback 함수
      this.props.onIncrement(habit);
    };

    handleDecrement = (habit) => {
        this.props.onDecrement(habit);
    };

    handleDelete = (habit) => {
        this.props.onDelete(habit);
    };

    handleAdd = () => {
        const habitsCount = this.state.habits.length;
        const add = [
            {id: habitsCount + 1,
            name: "la",
            count: 0}
        ];
        const habits = [...this.state.habits,add];
        this.setState({habits});
    };

    handleRest = () => {
        const habits = [
            {id: 1, name: 'Reading', count: 0},
            {id: 2, name: 'Running', count: 0},
            {id: 3, name: 'Coding', count: 0},
        ];
        let habitCount = 0;

        this.setState({habits});
        this.setState({habitCount});

    };

    render() {
        return ( 
            <>
                <Navbar habitCount={this.state.habitCount}/>
                <input className="habit-add" placeholder='Habit' name="inputhabit"/>
                <button className="habit-btn" onClick={this.handleAdd}>Add</button>
                <ul>
                    {this.state.habits.map(habit => (
                        <Habit
                            key={habit.id} /* habit이라는 props에 habit(map의 item) 전달, 배열인 경우 prop key값 지정 */
                            habit={habit}
                            onIncrement={this.handleIncrement}
                            onDecrement={this.handleDecrement}
                            onDelete={this.handleDelete} />
                    ))}
                </ul>
                <button className='habit-btn' onClick={this.handleRest}>Reset All</button>
            </>
        );
    }
}

export default Habits;
